import React from 'react'
import '../css/components/Button.css'

import { useSelector, useDispatch } from 'react-redux';
import { update_form_field } from '../actions/signup';

export default (props) => {
    const signupForm = useSelector(state => state.form);
    const dispatch = useDispatch();

    let inputProperties = {}

    if (props.validate) {
        inputProperties.onClick = () => {
            for (let index in props.validate) {
                alert(props.validate[index]);
            }
        }
    }else {
        onClick = props.onClick;
    }

    return (
        <button {...inputProperties} className={"affittoButton " + props.className} >
            {props.children}
        </button>
    )
}
