import React, { Component } from 'react'
//import Carousel from 'react-bootstrap/Carousel'

import FirstPage from './First'
import SecondPage from './Second'

class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = {currentPage: 1};
    }

    navegatePage = (pageNumber) => {
        this.setState({currentPage: pageNumber});
        window.scrollTo(0, 0);
    }

    render() {
        return <>
                    <div className={this.state.currentPage != 1 ? 'hide-page' : ''}>
                        <FirstPage btnNext={this.navegatePage.bind(this, 2)} />
                    </div>
                    <div className={this.state.currentPage != 2 ? 'hide-page' : ''}>
                        <SecondPage btnNext={this.navegatePage.bind(this, 2)} btnPrev={this.navegatePage.bind(this, 1)}/>
                    </div>
                </>;
    }
}

export default Signup;

