import React from 'react'
import '../css/Login.css';
import '../css/components/Error.css';


import TextField from '../components/TextField';
import Button from '../components/Button';

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Alert from 'react-bootstrap/Alert'

import { loginAlert } from '../actions/login';
import { useSelector, useDispatch } from 'react-redux';


export default (props) => {
    const dispatch = useDispatch();
    const loginForm = useSelector(state => state.login);

    const loginAction = () => {
        //REQUIRE FROM SERVER
        if (loginForm.email == "douglasciole@gmail.com" && loginForm.password == "1q2w3e4r5t") {
            localStorage.setItem("userData", JSON.stringify({
                "status": 1,
                "message": "Success",
                "userName": "Douglas Ciole",
                "accessToken": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOjEsInVzZXJOYW1lIjoiRG91Z2xhcyBDaW9sZSIsImlhdCI6MTU5MzczODU4MCwiZXhwIjoxNTkzNzM5MTgwfQ.LvuJSvGmZNgZRTP9cRbXx8huQiDtA537og5YanzBeaU",
                "refreshToken": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOjEsInVzZXJOYW1lIjoiRG91Z2xhcyBDaW9sZSIsImlhdCI6MTU5MzczODU4MH0.oDGzlcqx_tZZVSez7B_UrB_XrWidxm1ypawSaI0IjYg",
                "companyName": "ChangeME"
            }));

            window.location = "/menu";
        }else {
            dispatch(loginAlert(true));
            setTimeout(() => {
                dispatch(loginAlert(false));
            }, 3000);
        }
    }

    return (
        <>
            <div className="alert-message-container">
                <Alert show={loginForm.error} key='ewfwef'>
                    <div className="affitto-error-container">
                        <Container>
                            <Row className="menuRow">
                                <Col md={2} lg={2} sm={1} xs={1}></Col>
                                <Col md={8} lg={8} sm={10} xs={10}>
                                    <span>E-mail or Password is incorrect!</span>
                                </Col>
                            </Row>
                        </Container>
                    </div>
                </Alert>
            </div>

            <Row className="menuRow">
                <Col md={2} lg={2} sm={1} xs={1}></Col>
                <Col md={6} lg={6} sm={10} xs={10}>
                    <div className="login-title">
                        Welcome to the Administrative Portal
                    </div>
                </Col>
                <Col md={4} lg={4} sm={1} xs={1}></Col>
            </Row>


            <TextField linked="EMAIL" label="E-Mail" />
            <TextField linked="PASSWORD" type="password" label="Password" />

            <Row className="menuRow">
                <Col md={2} lg={2} sm={1} xs={1}></Col>
                <Col md={6} lg={6} sm={10} xs={10}>
                    <div className="login-btn-container">
                        <Button onClick={loginAction}>Login</Button>
                    </div>
                </Col>
                <Col md={4} lg={4} sm={1} xs={1}></Col>
            </Row>


        </>
    )
}
