import React from 'react'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import { useSelector } from 'react-redux';

import '../css/components/Error.css';

export default (props) => {
    const errorInfo = useSelector(state => state.error);

    //title={errorInfo["accountPage_title"]} show={errorInfo.accountPage_show}
    return (
        <div className={"affitto-error-container " + ((props.show == true)?"":"hide")}>
            <Container>
            <Row className="menuRow">
                <Col md={2} lg={2} sm={1} xs={1}></Col>
                <Col md={8} lg={8} sm={10} xs={10}>
                    <span>{props.title}</span>
                </Col>
            </Row>
        </Container>
        </div>
    )
}
