import React, { Component } from 'react'
import '../css/components/BigDisplay.css'

export class BigDisplay extends Component {
    render() {

        const bigDisplay = {
            padding: '85px 208px',
            backgroundColor: '#18474E',
            color: '#FFFFFF',
            textAlign: 'left',
            maxWIdth: '790px'
        }

        const bigDisplayTitle = {
            fontSize: '46px',
            fontWeight: 'bold',
            display: 'inline-block'
        }

        const bigDisplayText = {
            fontSize: '33px'
        }

        return (
            <div style={bigDisplay}>
                <span className='bigDisplayTitle'>Can you please enter your tax identification number?</span>
                <span style={bigDisplayText}>We need your company tax identification number to verify your business details.</span>
            </div>
        )
    }
}

export default BigDisplay
