import React from 'react'
import '../css/components/DateField.css'

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import { useSelector, useDispatch } from 'react-redux';
import { update_form_field } from '../actions/signup';

export default (props) => {
    const signupForm = useSelector(state => state.form);
    const dispatch = useDispatch();

    let inputProperties = {}

    if (props.linked) {
        inputProperties.value = signupForm[props.linked];
        inputProperties.onChange = (value) => {dispatch(update_form_field(props.linked, value))};
    }

    return (
    <Row className="menuRow">
        <Col md={2} lg={2} sm={1} xs={1}></Col>
        <Col md={6} lg={6} sm={10} xs={10}>
            <label className="textDIsplay">
                {props.label}
                {/* https://www.npmjs.com/package/react-datepicker */}
                <DatePicker
                    {...inputProperties}
                    // selected={signupForm.Date_Birth}
                    // onChange={(event) => {console.log(event); }}
                />
            </label>
        </Col>
        <Col md={4} lg={4} sm={1} xs={1}></Col>
    </Row>
    );
}
