let errorData = {
    accountPage_title: "",
    accountPage_show: false
}


const errorReducer = (state = errorData, action) => {
    let newState = { ...state }

    switch(action.type) {
        case "ACCOUNT":
            newState.accountPage_title = action.payload;
            newState.accountPage_show = action.show;
        break;
    }

    state = newState;
    return state;
}

export default errorReducer;
